-- ----------------------------
-- Table structure for `ke_access`
-- ----------------------------
DROP TABLE IF EXISTS `ke_access`;
CREATE TABLE `ke_access` (
  `id` int(100) NOT NULL COMMENT 'id',
  `title` varchar(200) NOT NULL COMMENT '权限名称',
  `name` varchar(200) NOT NULL COMMENT '权限名',
  `route` varchar(200) NOT NULL COMMENT '路由url',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of ke_access
-- ----------------------------

-- ----------------------------
-- Table structure for `ke_article`
-- ----------------------------
DROP TABLE IF EXISTS `ke_article`;
CREATE TABLE `ke_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(100) NOT NULL COMMENT '标题',
  `title_color` varchar(20) DEFAULT NULL,
  `body` mediumtext COMMENT '正文',
  `summary` varchar(200) NOT NULL COMMENT '摘要',
  `author` varchar(50) DEFAULT NULL COMMENT '作者',
  `fromto` varchar(50) DEFAULT NULL COMMENT '来源',
  `inputer` varchar(50) DEFAULT NULL COMMENT '录入者',
  `pid` varchar(50) DEFAULT '0' COMMENT '分类',
  `pid_name` varchar(100) DEFAULT NULL,
  `thumb` varchar(200) DEFAULT NULL COMMENT '缩略图',
  `hot` tinyint(1) NOT NULL DEFAULT '0' COMMENT '热门',
  `top` tinyint(1) NOT NULL DEFAULT '0' COMMENT '置顶',
  `sort` varchar(50) NOT NULL DEFAULT '50' COMMENT '排序',
  `keyword` varchar(100) DEFAULT NULL,
  `read_count` varchar(50) NOT NULL DEFAULT '1' COMMENT '访问次数',
  `created_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `id` (`id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=101 DEFAULT CHARSET=utf8;

